package be.pxl.multithreading.oef2;class CustomerGenerator extends Thread {    private static final int MAX_TIME = 5000;    private static final int MIN_TIME = 100;    private BankLine line;    private int count;    public CustomerGenerator(BankLine line) {        this.line = line;        count = 0;    }    public void run() {        while (true) {            try {                Customer aCustomer = new Customer(Integer.toString(count));                count++;                line.addCustomer(aCustomer);                int nextCustomerIn = MIN_TIME + (int) Math.round(Math.random() * (MAX_TIME - MIN_TIME));                Thread.sleep(nextCustomerIn);            } catch (InterruptedException ex) {                ex.printStackTrace();            }        }    }}